import React from 'react'
import {MonthNames, JobsById} from '../utilities/content'

class Projects extends React.Component {
  state = {
    loaded: false,
    selected_task: "none"
  };

  getJobData () {
    var months = {
      Unscheduled: []
    };
    var tasks_by_id = {};
    this.props.project_data.tasks.map((task_id, i) => {
      let task_insert = {
        ...(JobsById[task_id])
      };
      tasks_by_id[task_insert.id] = task_insert;
      if (!task_insert.start_date) {
        months.Unscheduled.push(task_insert);
        return true;
      }
      let task_month = task_insert.start_date.getMonth();
      if (!months[task_month]) {
        months[task_month] = [];
      }
      months[task_month].push(task_insert);
      return true;
    });
    return [tasks_by_id, months];
  }

  componentDidMount() {
    var tasks_by_id = this.getJobData();
    this.setState({
      tasks_by_id: tasks_by_id[0],
      months: tasks_by_id[1],
      selected_task:this.props.selected_task
    });
  }

  componentDidUpdate(prevProps) {
    if (this.props.selected_task !== prevProps.selected_task) {
      this.setState({
        selected_task:this.props.selected_task
      });
    }
    if (this.props.project_data.id !== prevProps.project_data.id) {
      var tasks_by_id = this.getJobData();
      this.setState({
        tasks_by_id: tasks_by_id[0],
        months: tasks_by_id[1],
        selected_task:this.props.selected_task
      });
    }
  }


  selectJob = (e) => {
    let id=e.currentTarget.dataset.id;

    // if (this.state.selected_task!==id) {
      // this.setState({
      //   selected_task:id
      // });
      this.props.onSelectProject(this.props.id, id);
    // }
  }

  handleCloseButton = (e) => {
    this.props.onSelectProject(null, null);
  }


  render() {
    var display_months = [];
    var today = new Date();
    if (this.state.months) {
      var month_keys = Object.keys(this.state.months);
      var months = this.state.months;
      var after_today = false;
      month_keys.sort(function(a, b){return months[a] - months[b]});
      for (var i=0; i<month_keys.length; i++) {
        var month = this.state.months[month_keys[i]];
        month.sort(function(a, b){return a.start_date - b.start_date});
        for (var j=0; j<month.length; j++) {
          var task = month[j];
          var mark_today = "";
          if (!after_today && task.start_date && task.start_date >= new Date(today.getFullYear(), today.getMonth(), today.getDate())) {
            after_today = true;
            mark_today = " after-today";
          }
          display_months.push((
            <li
              key={task.id}
              data-id={task.id}
              className={
                (task.progress === 100 ? "complete" : task.progress > 0 ? "progress" : "") +
                (" "+month_keys[i]) +
                (task.start_date && (task.start_date.toDateString() === (new Date()).toDateString())?" today":"") +
                mark_today +
                (j === month.length-1?" last-of-month":"") +
                (task.id===this.state.selected_task?" selected":" not-selected") +
                ((j<month.length-1) && month[j+1].id===this.state.selected_task?" task-before-selected":"")
              }
              onClick={this.selectJob}
            >
              {
                j===0?<h3>{MonthNames[month_keys[i]] || month_keys[i]}</h3>:[]
              }
              <span className="h-line" />
              <span className="selected-arrow" />
              <span className="date">{(task.start_date ? task.start_date.getDate() : "")}</span>
              <div className="task-box">
                <h4><i className={task.icon}></i><span className="task-name">{task.name}</span></h4>
              </div>
              <span className="status">
                <span className="progress-bar" style={{transform:"scaleX("+(task.progress/100)+")"}}></span>
                <span className="status-text">{
                  task.progress===100 ?
                  (<i className="fas fa-check"></i>):
                  task.progress===0 ?
                  (""):
                  (task.progress + "%")
                }</span>
              </span>
            </li>
          ));
        };
      };
    }
    return (
      <article
        className={"project" +
          (this.props.last_selected?" last_selected":"")
      }>
        <header>
          <h2>{this.props.project_data.name}</h2>

            { this.props.selected_task && (
              <div className="button-menu">
                <button onClick={this.handleCloseButton} className="icon-button"><i className="fas fa-times"></i></button>
              </div>
            )}
        </header>
        <div className="header-color" style={{
          backgroundColor: this.props.project_data.color || "black"
        }} />
        <div className="timeline">
          {display_months}
        </div>
      </article>
    );
  }
}

export default Projects
