import React from 'react'

class TaskEditor extends React.Component {
  state = {
    tasks:[],
    mode: "task",
    currently_editing: 0
  };

  componentDidMount() {
    this.setState({
      tasks: [...this.props.selected_job_data.tasks]
    });
  }

  componentDidUpdate(prevProps) {
    if (this.props.selected_job_data && this.props.selected_job !== prevProps.selected_job) {
      this.setState({
        tasks: [...this.props.selected_job_data.tasks]
      });
    }
  }

  handleTaskClick = (e) => {
    let id=e.currentTarget.dataset.id;
    for (var t=0; t<this.props.selected_job_data.tasks.length; t++) {
      var task = this.props.selected_job_data.tasks[t];
      if (task.id === id) {
        this.props.selected_job_data.tasks[t].completed = !task.completed;
      }
    }
    this.setState({
      tasks: [...this.props.selected_job_data.tasks]
    });
  }

  handleDelete = (e) => {
    e.preventDefault();
    e.stopPropagation();
    let id=e.currentTarget.parentNode.parentNode.dataset.id;
    let tasks = [];
    for (var t=0; t<this.props.selected_job_data.tasks.length; t++) {
      var task = this.props.selected_job_data.tasks[t];
      if (task.id !== id) {
        tasks.push(this.props.selected_job_data.tasks[t]);
      }
    }
    this.updateTasks(tasks);
  }

  handleEdit = (e) => {
    e.preventDefault();
    e.stopPropagation();
    let id=e.currentTarget.parentNode.parentNode.dataset.id;

    this.setState({
      currently_editing: id
    });
    // let tasks = [];
    // for (var t=0; t<this.props.selected_job_data.tasks.length; t++) {
    //   var task = this.props.selected_job_data.tasks[t];
    //   if (task.id === id) {
    //     tasks.push(this.props.selected_job_data.tasks[t]);
    //   }
    // }
    // this.updateTasks(tasks);
  }

  updateTasks (tasks) {
    this.props.selected_job_data.tasks = tasks;
    this.setState({
      tasks: [...this.props.selected_job_data.tasks]
    });
  }

  changeMode (mode) {
    this.setState({
      mode: mode
    });
  }

  getMenu () {
    const menu = this.state.mode === "task" ? (
      <div className="menu">
        <div>
          <button onClick={(e)=>{this.handleAddTask(e)}}><i className="fas fa-plus"></i><span>Add Task</span></button>
        </div>
      </div>
    ):(
      <div className="menu">
        <div>
          <button onClick={(e)=>{this.handleDelete()}}><i className="fas fa-trash"></i><span>Delete Tasks</span></button>
          <button onClick={(e)=>{this.changeMode("task")}}><span>Cancel</span></button>
        </div>
      </div>
    );
    return menu;
  }


  render() {
    let tasks = [...this.state.tasks];
    let task_nodes = [];

    for (var t=0; t<tasks.length; t++) {
      task_nodes.push((
        <li key={this.props.selected_job + "_" + tasks[t].id} className={"task" + (tasks[t].completed ? " completed" : "")} data-id={tasks[t].id} onClick={this.handleTaskClick}>
          <h3>
            <span className="icon-holder"><i className="fas fa-check-circle check"></i><i className="far fa-circle"></i></span>
            {this.state.currently_editing === tasks[t].id ? (
              <input className="task-name" defaultValue={tasks[t].name} />
            ):(
              <span className="task-name">{tasks[t].name}</span>
            )}
          </h3>
          {this.state.currently_editing === tasks[t].id ? (
            <div>
              <button className="icon-button" onClick={this.handleEdit} title="Apply"><i className="fas fa-check"></i></button>
              <button className="icon-button" onClick={this.handleEdit} title="Cancel"><i className="fas fa-times"></i></button>
            </div>
          ):(
            <div>
              <button className="icon-button" onClick={this.handleEdit} title="Edit Task"><i className="fas fa-edit"></i></button>
              <button className="icon-button" onClick={this.handleDelete} title="Delete Task"><i className="fas fa-trash"></i></button>
            </div>
          )}
        </li>
      ));
    }

    return (
      <div className={"task-editor" + (this.state.loaded?"":" loading")} >
        {this.getMenu()}
        <div className="task-list">
          <ul>{task_nodes}</ul>
        </div>

      </div>
    );
  }
}

export default TaskEditor
