import React from 'react'
import Editor from './editor'
import Calendar from './calendar'
import ModalCalendar from './modal-calendar'
import {MonthNames} from '../utilities/content'

class Projects extends React.Component {
  state = {
    loaded: false,
    selected_task: "none",
    detail_mode: "note",
    show_selected_task: false
  };



  componentDidMount() {
  }

  SelectTask = (e) => {
    let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.currentTarget.dataset.id);

    this.setState({
      show_selected_task:(this.state.selected_task===id?!this.state.show_selected_task:true),
      show_selected_task_content: true
    });
    if (this.state.selected_task!==id) {
      this.setState({
        selected_task:id
      });
    }
    this.props.onSelectProject(this.props.id);
  }

  render() {


    return (
      <div className="scheduler">

      </div>
    );
  }
}

export default Projects
