import React from 'react'
import Editor from './editor'
import Calendar from './calendar'
import ModalCalendar from './modal-calendar'

class Projects extends React.Component {
  state = {
    loaded: false,
    selected_task: "none",
    detail_mode: "note",
    show_selected_task: false
  };
  month_names = [
    "January",
    "Fabruary",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December"
  ];

  ////TODO: Include Time intervals for indivitual day schedules

  data = [
    {
      start_date: new Date(2018,9,20),
      name: "Sign Contract",
      id: 1,
      icon: "fas fa-file-signature",
      progress: 100
    },
    {
      start_date: new Date(2018,9,22),
      name: "Pre Deposit",
      id: 2,
      icon: "fas fa-dollar-sign",
      progress: 100
    },
    {
      start_date: new Date(2018,9,12),
      name: "Estimation",
      id: 3,
      icon: "fas fa-balance-scale",
      progress: 100
    },
    {
      start_date: new Date(2018,9,29),
      name: "Demolition",
      id: 4,
      icon: "fas fa-hammer",
      progress: 40
    },
    {
      start_date: new Date(2018,9,28),
      name: "Protection",
      id: 5,
      icon: "fas fa-tape",
      progress: 78
    },
    {
      start_date: new Date(2018,9,30),
      name: "Tile Install",
      id: 6,
      icon: "fas fa-toolbox",
      progress: 0
    },
    {
      start_date: new Date(2018,10,1),
      name: "Plumbing",
      id: 7,
      icon: "fas fa-tint",
      progress: 0
    },
    {
      start_date: new Date(2018,10,4),
      name: "Clean Up",
      id: 8,
      icon: "fas fa-broom",
      progress: 0
    },
    {
      start_date: null,
      name: "Inspection",
      id: 9,
      icon: "fas fa-clipboard-list",
      progress: 0
    },
    {
      start_date: null,
      name: "Final Payment",
      id: 0,
      icon: "fas fa-dollar-sign",
      progress: 0
    }
  ];

  componentDidMount() {
  }

  SelectTask = (e) => {
    let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.currentTarget.dataset.id);

    this.setState({
      show_selected_task:(this.state.selected_task===id?!this.state.show_selected_task:true),
      show_selected_task_content: true
    });
    if (this.state.selected_task!==id) {
      this.setState({
        selected_task:id
      });
    }
    this.props.onSelectProject(this.props.id);
  }

  scheduleTask() {
    // window.openModal(<ModalCalendar />, ()=>{
    //   console.log(this);
    // });
    this.setState({
      detail_mode: "schedule"
    });
  }

  takeNotes() {
    this.setState({
      detail_mode: "note"
    });
  }

  render() {
    var months = {
      Unassigned: []
    };
    var tasks_by_id = {};
    var display_months = [];
    this.data.map((task, i) => {
      let task_insert = {
        ...task
      };
      tasks_by_id[task_insert.id] = task_insert;
      if (!task.start_date) {
        months.Unassigned.push(task_insert);
        return true;
      }
      let task_month = task.start_date.getMonth();
      if (!months[task_month]) {
        months[task_month] = [];
      }
      months[task_month].push(task_insert);
      return true;
    });
    var month_keys = Object.keys(months);
    month_keys.sort(function(a, b){return months[a] - months[b]});
    for (var i=0; i<month_keys.length; i++) {
      var month = months[month_keys[i]];
      month.sort(function(a, b){return a.start_date - b.start_date});
      for (var j=0; j<month.length; j++) {
        var task = month[j];
        display_months.push((
          <li key={task.id} data-id={task.id} className={
            (task.progress === 100 ? "complete" : task.progress > 0 ? "progress" : "") +
            (" "+month_keys[i]) +
            (task.start_date && (task.start_date.toDateString() === (new Date()).toDateString())?" today":"") +
            (j === month.length-1?" last-of-month":"") +
            (task.id===this.state.selected_task&&this.state.show_selected_task?" selected":(this.state.show_selected_task===false?" none-selected":" not-selected")) +
            ((j<month.length-1) && month[j+1].id===this.state.selected_task && this.state.show_selected_task?" task-before-selected":"")
            // eslint-disable-next-line
          } onClick={this.SelectTask}>
            {
              j===0?<h3>{this.month_names[month_keys[i]] || month_keys[i]}</h3>:[]
            }
            <span className="h-line" />
            <span className="selected-arrow" />
            <span className="date">{(task.start_date ? task.start_date.getDate() : "")}</span>
            <div className="task-box">
              <h4><i className={task.icon}></i><span className="task-name">{task.name}</span></h4>
            </div>
            <span className="status">
              <span className="progress-bar" style={{transform:"scaleX("+(task.progress/100)+")"}}></span>
              <span className="status-text">{
                task.progress===100 ?
                (<i className="fas fa-check"></i>):
                task.progress===0 ?
                (""):
                (task.progress + "%")
              }</span>
            </span>
          </li>
        ));
      };
      // display_months.push((
      //   <ul className={"timeline-month " + month_keys[i].toLowerCase()}>
      //
      //   </ul>
      // ));
    };

    return (
      <article className={"project" +
        ((this.state.show_selected_task===false || !this.props.selected_project)?"":" open")
      }>
        <header>
          <h2>Project Name</h2>
        </header>
        <div className="timeline">
          {display_months}
        </div>
        <div className={"task-detail-panel" +
          ((this.state.show_selected_task===false || !this.props.selected_project)?"":" open") +
          (this.state.show_selected_task_content===false?"":" show-content")
        }>
          { this.state.selected_task==="none"?[]:(
            <div>
              <div>
                <header>
                  <h2>{tasks_by_id[this.state.selected_task].name}</h2>
                  <div>
                    <button className={this.state.detail_mode === "note"?"active":""} onClick={()=>{this.takeNotes()}}>take notes</button>
                    <button className={this.state.detail_mode === "schedule"?"active":""} onClick={()=>{this.scheduleTask()}}>schedule task</button>
                  </div>
                </header>
                <div className="tab-panel">
                  <div className={this.state.detail_mode === "note"?"show":""}>
                    <h3>notes</h3>
                    <Editor
                      value="These are some notes!"
                      onChange={(value)=>{console.log(value);}}
                    />
                  </div>
                  <div className={this.state.detail_mode === "schedule"?"show":""}>
                    <h3>schedule</h3>
                    <Calendar />
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </article>
    );
  }
}

export default Projects
