import React from 'react';
import Project from './project';
import TaskDetails from './task-details';
import {ProjectList} from '../utilities/content'

class Projects extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loaded: false,
      selected_task: null
    };
  }
  componentDidMount() {
    setTimeout(()=>{
      var projects_by_id = {};
      for (var p=0;p<ProjectList.length; p++) {
        projects_by_id[ProjectList[p].id]=ProjectList[p];
      }
      this.setState({
        loaded: true,
        projects_by_id: projects_by_id
      });
    }, 50);
  }
  handleTaskClick = (project_id, id) => {
    this.selectProject(project_id, id);
  }
  selectProject (id, task) {
    this.setState({
      selected_project: id,
      selected_task: task
    });
  }
  render() {
    let projects = [];

    for (let p=0; p<ProjectList.length; p++) {
      projects.push((
        <div key={"project-"+ProjectList[p].id} className={"card" + (p<5?" d"+(p+1):" d5")}>
          <Project
            id={ProjectList[p].id}
            project_data={ProjectList[p]}
            onSelectProject={(id, task)=>{
              this.selectProject(id, task);
            }}
            selected_project={this.state.selected_project===ProjectList[p].id}
          />
        </div>
      ));
    }

    return (
      <div className={this.state.loaded?"loaded":"unloaded"}>
        {this.state.selected_task?(
          <div id="project-details" className={"" + (this.state.selected_task?" show":"")}>
            <div className="card">
              <Project
                id={this.state.selected_project}
                project_data={this.state.projects_by_id[this.state.selected_project]}
                onSelectProject={(id, task)=>{
                  this.selectProject(id, task);
                }}
                selected_project={true}
                selected_task={this.state.selected_task.id}
              />
            </div>
            <div className="card">
              <TaskDetails
                selected_task={this.state.selected_task}
                handleTaskClick={this.handleTaskClick}
              />
            </div>
          </div>
        ) : (
          <div className={"project-list"+(this.state.selected_task?"":" show")}>
            {projects}
          </div>
        )}
      </div>
    );
  }
}

export default Projects
