import React from 'react'
import UserHeatmap from './user-heatmap'
import UserHeatmapDates from './user-heatmap-dates'
import {DaysInMonth} from '../utilities/date'
import {MonthNames, MonthNamesShort, Users} from '../utilities/content'

class Scheduler extends React.Component {
  state = {
    loaded: false,
    sort: "last_name",
    trade_filter: "all",
    date: new Date(),
    selected_day: 0,
    selected_index: 0
  };

  componentDidMount() {
    const users = [...Users];
    setTimeout(()=>{
      const trades = {};
      for (var i=0; i < users.length; i++) {
        trades[users[i].trade_name] = !!users[i].trade_name;
      }
      this.setState({
        trades: trades,
        users: [...users],
        loaded: true
      });
      if (this.props.selected_task_data.start_date) {
        this.gotoDate(this.props.selected_task_data.start_date);
      }
      else {
        this.handleClearSelectedDay();
      }
    }, 100);
  }

  componentDidUpdate(prevProps) {
    if (this.props.selected_task !== prevProps.selected_task) {
      if (this.props.selected_task_data.start_date) {
        this.gotoDate(this.props.selected_task_data.start_date);
      }
      else {
        this.handleClearSelectedDay();
      }
    }
  }

  selectSort = (e) => {
    // let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.target.value);
    this.setState({sort: e.target.value});
  }

  selectTradeFilter = (e) => {
    // let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.target.value);
    this.setState({trade_filter: e.target.value});
  }

  handleTaskClick = (project_id, id) => {
    this.props.handleTaskClick(project_id, id);
  }

  handleDayClick = (day, index) => {
    this.setState({
      selected_day: day,
      selected_index: index
    });
  }

  handleClearSelectedDay = (e) => {
    this.setState({
      selected_day: 0
    });
  }

  handleNextDay = (e) => {
    let selected_day = this.state.selected_day;
    var current_month = this.state.date;
    if (selected_day === DaysInMonth(current_month.getMonth(), current_month.getFullYear())) {
      this.handleNextMonth();
      selected_day = 1;
    } else {
      selected_day++;
    }
    this.setState({
      selected_day: selected_day
    });
  }
  handlePrevDay = (e) => {
    let selected_day = this.state.selected_day;
    let new_month;
    if (selected_day === 1) {
      new_month = this.handlePrevMonth();
      selected_day = DaysInMonth(new_month.getMonth(), new_month.getFullYear());
    } else {
      selected_day--;
    }
    this.setState({
      selected_day: selected_day
    });
  }

  handleGotoToday = (e) => {
    var today = new Date();
    this.gotoDate(today);
  }

  gotoDate = (date) => {
    this.setState({
      date: date,
      selected_day: date.getDate()
    });
  }

  handleNextMonth = (e) => {
    var now = this.state.date;
    var current;
    if (now.getMonth() === 11) {
      current = new Date(now.getFullYear() + 1, 0, 1);
    } else {
      current = new Date(now.getFullYear(), now.getMonth() + 1, 1);
    }
    this.setState({
      date: current
    });
    return current;
  }

  handlePrevMonth = (e) => {
    var now = this.state.date;
    var current;
    if (now.getMonth() === 0) {
      current = new Date(now.getFullYear() - 1, 11, 1);
    } else {
      current = new Date(now.getFullYear(), now.getMonth() - 1, 1);
    }
    this.setState({
      date: current
    });
    return current;
  }

  render() {
    const user_nodes = [];
    const user_dates = [];
    const trades = [];

    if (this.state.loaded) {
      let users = [...this.state.users];
      let trade_names = Object.keys(this.state.trades);
      trade_names.sort(function(a, b){
        var x = a.toLowerCase();
        var y = b.toLowerCase();
        if (x < y) {return -1;}
        if (x > y) {return 1;}
        return 0;
      });
      for (var t=0; t<trade_names.length; t++) {
        trades.push((
          <option key={trade_names[t]} value={trade_names[t]}>{trade_names[t]}</option>
        ));
      }
      let sort_value = this.state.sort;
      users.sort(function(a, b){
        var x = a[sort_value] ? a[sort_value].toLowerCase() : "zzzzzz";
        var y = b[sort_value] ? b[sort_value].toLowerCase() : "zzzzzz";
        if (x < y) {return -1;}
        if (x > y) {return 1;}
        return 0;
      });
      for (var i=0; i<users.length; i++) {
        if (this.state.trade_filter === "all" || users[i].trade_name === this.state.trade_filter){
          user_nodes.push((
            <li key={"user-"+i}>
              <h3>{users[i].first_name + " " + users[i].last_name}</h3>
              <h4>{users[i].trade_name}</h4>
            </li>
          ));
          user_dates.push((
            <li key={"user-"+users[i].user_id}>
              <UserHeatmap
                handleDayClick={this.handleDayClick}
                handleTaskClick={this.handleTaskClick}
                selected_day={this.state.selected_day}
                selected_index={this.state.selected_index}
                user={users[i]}
                month={this.state.date.getMonth()}
                year={this.state.date.getFullYear()}
                selected_task={this.props.selected_task}
                selected_task_data={this.props.selected_task_data}
                index={i}
              />
            </li>
          ));
        }
      }
    }

    return (
      <div className={"scheduler" + (this.state.loaded?"":" loading")} >
        <div className="menu">
          <div>
            <i className="fas fa-sort-alpha-down"></i>
            <select onChange={this.selectSort}>
              <option value="last_name">sort by last name</option>
              <option value="first_name">sort by first name</option>
              <option value="trade_name">sort by trade</option>
            </select>

            <i className="fas fa-filter"></i>
            <select onChange={this.selectTradeFilter}>
              <option value="all">all</option>
              {trades}
            </select>
          </div>
          {this.state.selected_day ? (
            <div>
              <button onClick={(e)=>{this.handlePrevDay(e)}}><i className="fas fa-angle-left"></i><span>Previous Day</span></button>
              <button onClick={(e)=>{this.handleClearSelectedDay(e)}}><i className="fas fa-angle-up"></i><span>{MonthNames[this.state.date.getMonth()]}</span></button>
              <button onClick={(e)=>{this.handleNextDay(e)}}><span>Next Day</span><i className="fas fa-angle-right"></i></button>
            </div>
          ) : (
            <div>
              <button onClick={(e)=>{this.handlePrevMonth(e)}}><i className="fas fa-angle-left"></i><span>Previous Month</span></button>
              <button onClick={(e)=>{this.handleGotoToday(e)}}><span>Today</span></button>
              <button onClick={(e)=>{this.handleNextMonth(e)}}><span>Next Month</span><i className="fas fa-angle-right"></i></button>
            </div>
          )}
        </div>
        <div className="schedule-grid schedule-grid-header">
          <ul className="user-names">
            <li className="month-name"><h3>{this.state.selected_day ? MonthNamesShort[this.state.date.getMonth()] + " " + this.state.selected_day : (MonthNames[this.state.date.getMonth()])}</h3><h4>{this.state.date.getFullYear()}</h4></li>
          </ul>
          <ul className="heat-maps">
            <li>
              <UserHeatmapDates
                handleDayClick={this.handleDayClick}
                selected_day={this.state.selected_day}
                month={this.state.date.getMonth()}
                year={this.state.date.getFullYear()}
                selected_task={this.props.selected_task}
                selected_task_data={this.props.selected_task_data}
              />
            </li>
          </ul>
        </div>
        <div className="schedule-grid">
          <ul className="user-names">
          {
            this.state.loaded ?
            user_nodes :
            [
              (<li key="placeholder-1"></li>),
              (<li key="placeholder-2"></li>),
              (<li key="placeholder-3"></li>),
              (<li key="placeholder-4"></li>),
              (<li key="placeholder-5"></li>),
              (<li key="placeholder-6"></li>),
              (<li key="placeholder-7"></li>),
              (<li key="placeholder-8"></li>),
              (<li key="placeholder-9"></li>),
              (<li key="placeholder-10"></li>)
            ]
          }
          </ul>
          <ul className="heat-maps">
          {
            this.state.loaded ?
            user_dates :
            [
              (<li key="placeholder-1"></li>),
              (<li key="placeholder-2"></li>),
              (<li key="placeholder-3"></li>),
              (<li key="placeholder-4"></li>),
              (<li key="placeholder-5"></li>),
              (<li key="placeholder-6"></li>),
              (<li key="placeholder-7"></li>),
              (<li key="placeholder-8"></li>),
              (<li key="placeholder-9"></li>),
              (<li key="placeholder-10"></li>)
            ]
          }
          </ul>
        </div>
      </div>
    );
  }
}

export default Scheduler
