import React from 'react'
import {WEEKEND, DAILY_WORK_HOURS} from '../config/account-settings'

class Scheduler extends React.Component {
  state = {

  };

  componentDidMount() {
    const days = [];
    for (var d=0; d<this.daysInMonth(this.props.month, this.props.year); d++) {
      days.push(Math.min(Math.round(Math.random()*DAILY_WORK_HOURS),DAILY_WORK_HOURS));
    }
    setTimeout(()=>{
      this.setState({
        days: days
      });
    }, 1000);
  }

  SelectDate = (e) => {
    // let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.target.value);
    this.setState({sort: e.target.value});
  }

  daysInMonth = (month, year) => { // Use 1 for January, 2 for February, etc.
    return new Date(year, month+1, 0).getDate();
  }

  render() {
    const cells = [];
    const classes = [
      "tasks-0",
      "tasks-1",
      "tasks-2",
      "tasks-3",
      "tasks-4",
      "tasks-5",
      "tasks-6",
      "tasks-7",
      "tasks-8"
    ];

    if (this.state.days) {
      for (var i=0; i<this.daysInMonth(this.props.month, this.props.year); i++) {
        const value = this.state.days[i];
        const weekend = "";
        //TODO remove this:
        const current_date = new Date(this.props.year, this.props.month, (i+1));
        for (var w=0; w<WEEKEND.length; w++) {
          if (current_date.getDay() === WEEKEND[w]) {
            value = 0;
            weekend = " weekend";
          }
        }
        cells.push((
          <li key={this.props.user.user_id + "_" + i} className={"cell " + (classes[Math.min(Math.round(value*8/DAILY_WORK_HOURS),classes.length-1)]) + weekend}>{value?value+" hrs":""}</li>
        ));
      }
    }

    return (
      <ul>
        {cells}
      </ul>
    );
  }
}

export default Scheduler
