import React from 'react'
import {MonthNames} from '../utilities/content'

class Projects extends React.Component {
  state = {
    loaded: false,
    selected_task: "none",
    detail_mode: "note",
    show_selected_task: false
  };


  ////TODO: Include Time intervals for indivitual day schedules

  data = [
    {
      start_date: null,
      assigned_user: Math.round(Math.random()*12),
      name: "Sign Contract",
      id: 1,
      icon: "fas fa-file-signature",
      progress: 0
    },
    {
      start_date: null,
      assigned_user: Math.round(Math.random()*12),
      name: "Pre Deposit",
      id: 2,
      icon: "fas fa-dollar-sign",
      progress: 0
    },
    {
      start_date: null,
      assigned_user: Math.round(Math.random()*12),
      name: "Estimation",
      id: 3,
      icon: "fas fa-balance-scale",
      progress: 0
    },
    {
      start_date: null,
      assigned_user: Math.round(Math.random()*12),
      name: "Demolition",
      id: 4,
      icon: "fas fa-hammer",
      progress: 0
    },
    {
      start_date: null,
      assigned_user: Math.round(Math.random()*12),
      name: "Protection",
      id: 5,
      icon: "fas fa-tape",
      progress: 0
    },
    {
      start_date: null,
      assigned_user: Math.round(Math.random()*12),
      name: "Tile Install",
      id: 6,
      icon: "fas fa-toolbox",
      progress: 0
    },
    {
      start_date: null,
      name: "Plumbing",
      id: 7,
      icon: "fas fa-tint",
      progress: 0,
      default_trade: "Plumber"
    },
    {
      start_date: null,
      name: "Clean Up",
      id: 8,
      icon: "fas fa-broom",
      progress: 0
    },
    {
      start_date: null,
      name: "Inspection",
      id: 9,
      icon: "fas fa-clipboard-list",
      progress: 0
    },
    {
      start_date: null,
      name: "Final Payment",
      id: 0,
      icon: "fas fa-dollar-sign",
      progress: 0
    }
  ];



  componentDidMount() {
    var months = {
      Unassigned: []
    };
    var tasks_by_id = {};
    this.data.map((task, i) => {
      let task_insert = {
        ...task
      };
      tasks_by_id[task_insert.id] = task_insert;
      if (!task.start_date) {
        months.Unassigned.push(task_insert);
        return true;
      }
      let task_month = task.start_date.getMonth();
      if (!months[task_month]) {
        months[task_month] = [];
      }
      months[task_month].push(task_insert);
      return true;
    });
    this.setState({
      tasks_by_id: tasks_by_id,
      months: months
    });
  }


  SelectTask = (e) => {
    let id=parseInt(e.currentTarget.dataset.id,10);

    this.setState({
      show_selected_task:(this.state.selected_task===id?!this.state.show_selected_task:true),
      show_selected_task_content: true
    });
    if (this.state.selected_task!==id) {
      this.setState({
        selected_task:id
      });
      this.props.onSelectProject(this.props.id, this.state.tasks_by_id[id]);
      console.log(this.state.tasks_by_id[id]);
    } else {
      this.props.onSelectProject(this.props.id, null);
    }
    console.log(this.state.tasks_by_id[id]);
  }


  render() {
    var display_months = [];

    if (this.state.months) {
      var month_keys = Object.keys(this.state.months);
      month_keys.sort(function(a, b){return this.state.months[a] - this.state.months[b]});
      for (var i=0; i<month_keys.length; i++) {
        var month = this.state.months[month_keys[i]];
        month.sort(function(a, b){return a.start_date - b.start_date});
        for (var j=0; j<month.length; j++) {
          var task = month[j];
          display_months.push((
            <li
              key={task.id}
              data-id={task.id}
              style={{
                backgroundColor: this.props.project_data.color
              }}
              className={
                (task.progress === 100 ? "complete" : task.progress > 0 ? "progress" : "") +
                (" "+month_keys[i]) +
                (task.start_date && (task.start_date.toDateString() === (new Date()).toDateString())?" today":"") +
                (j === month.length-1?" last-of-month":"") +
                (task.id===this.state.selected_task&&this.state.show_selected_task?" selected":(this.state.show_selected_task===false?" none-selected":" not-selected")) +
                ((j<month.length-1) && month[j+1].id===this.state.selected_task && this.state.show_selected_task?" task-before-selected":"")
              }
              onClick={this.SelectTask}
            >
              {
                j===0?<h3>{MonthNames[month_keys[i]] || month_keys[i]}</h3>:[]
              }
              <span className="h-line" />
              <span className="selected-arrow" />
              <span className="date">{(task.start_date ? task.start_date.getDate() : "")}</span>
              <div className="task-box">
                <h4><i className={task.icon}></i><span className="task-name">{task.name}</span></h4>
              </div>
              <span className="status">
                <span className="progress-bar" style={{transform:"scaleX("+(task.progress/100)+")"}}></span>
                <span className="status-text">{
                  task.progress===100 ?
                  (<i className="fas fa-check"></i>):
                  task.progress===0 ?
                  (""):
                  (task.progress + "%")
                }</span>
              </span>
            </li>
          ));
        };
        // display_months.push((
        //   <ul className={"timeline-month " + month_keys[i].toLowerCase()}>
        //
        //   </ul>
        // ));
      };
    }
    return (
      <article className={"project" +
        ((this.state.show_selected_task===false || !this.props.selected_project)?"":" open")
      }>
        <header>
          <h2>{this.props.project_data.name}</h2>
        </header>
        <div className="timeline">
          {display_months}
        </div>
      </article>
    );
  }
}

export default Projects
