import React from 'react'

class Scheduler extends React.Component {
  state = {
    loaded: false,
    sort: "last_name",
    trade_filter: null
  };

  example_user_data = [
    {
      first_name: "Luke",
      last_name: "Skywalker",
      trade_name: "Plumber"
    },
    {
      first_name: "General",
      last_name: "Akkbar",
      trade_name: "General Manager"
    },
    {
      first_name: "Obi-wan",
      last_name: "Kenobi",
      trade_name: "Technician"
    },
    {
      first_name: "Han",
      last_name: "Solo",
      trade_name: "General Contractor"
    },
    {
      first_name: "Lando",
      last_name: "Calrissian",
      trade_name: "Project Manager"
    }
  ];



  componentDidMount() {
    setTimeout(()=>{
      const trades = {};
      for (var i=0; i < this.example_user_data.length; i++) {
        trades[this.example_user_data[i].trade_name] = !!this.example_user_data[i].trade_name;
      }
      this.setState({
        trades: trades,
        users: [...this.example_user_data],
        loaded: true
      });
    }, 1000);
  }

  SelectSort = (e) => {
    // let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.target.value);
    this.setState({sort: e.target.value});
  }

  SelectTradeFilter = (e) => {
    // let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.target.value);
    this.setState({trade_filter: e.target.value});
  }

  render() {
    const user_nodes = [];
    const trades = [];

    if (this.state.loaded) {
      let users = [...this.state.users];
      let trade_names = Object.keys(this.state.trades);
      for (var t=0; t<trade_names.length; t++) {
        trades.push((
          <option value={trade_names[t]}>{trade_names[t]}</option>
        ));
      }
      let sort_value = this.state.sort;
      users.sort(function(a, b){
        var x = a[sort_value].toLowerCase();
        var y = b[sort_value].toLowerCase();
        if (x < y) {return -1;}
        if (x > y) {return 1;}
        return 0;
      });
      for (var i=0; i<users.length; i++) {
        if (!this.state.trade_filter || users[i].trade_name === this.state.trade_filter){
          user_nodes.push((
            <li key={"user-"+i}>
              <h3>{users[i].first_name + " " + users[i].last_name}</h3>
              <h4>{users[i].trade_name}</h4>
            </li>
          ));
        }
      }
    }

    return (
      <div className={"scheduler" + this.state.loaded?"":" loading"} >
        {
          this.state.loaded ?
          (<div>
            <select onChange={this.SelectSort}>
              <option value="last_name">Last Name</option>
              <option value="first_name">First Name</option>
              <option value="trade_name">Trade</option>
            </select>
            <select onChange={this.SelectTradeFilter}>
              {trades}
            </select>
          </div>) :
          []
        }
        <ul>
        {
          this.state.loaded ?
          user_nodes :
          [
            (<li key="placeholder-1"></li>),
            (<li key="placeholder-2"></li>),
            (<li key="placeholder-3"></li>),
            (<li key="placeholder-4"></li>),
            (<li key="placeholder-5"></li>),
            (<li key="placeholder-6"></li>),
            (<li key="placeholder-7"></li>),
            (<li key="placeholder-8"></li>),
            (<li key="placeholder-9"></li>),
            (<li key="placeholder-10"></li>)
          ]
        }
        </ul>
      </div>
    );
  }
}

export default Scheduler
