import React from 'react'
import {DaysInMonth, SameDay} from '../utilities/date'
import {WeekDaysShort} from '../utilities/content'

class Scheduler extends React.Component {
  state = {

  };


  SelectDate = (e) => {
    // let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.target.value);
    this.setState({sort: e.target.value});
  }

  HandleDayClick = (e) => {
    const id=parseInt(e.currentTarget.dataset.id,10);
    this.props.HandleDayClick(id)
  }

  render() {
    const cells = [];
    const day_cells = [];

    for (var i=0; i<DaysInMonth(this.props.month, this.props.year); i++) {
      var current_date = new Date(this.props.year, this.props.month, (i+1));
      cells.push((
        <li data-id={(i+1)} className={
          "cell" +
          ((current_date.toDateString() === (new Date()).toDateString()) ? " today" : "") +
          (this.props.task.start_date && SameDay(this.props.task.start_date, current_date) ? " assigned" : "")
        } onClick={this.HandleDayClick}>
          <span>{i+1}</span>
          <small>{WeekDaysShort[current_date.getDay()]}</small>
        </li>
      ));
    }

    if (this.props.selected_day) {
      for (var h=1; h<=24; h++) {
        const current_time = new Date(this.props.year, this.props.month, this.props.selected_day, h);
        day_cells.push((
          <li className={
            "day-cell"
          }>
            <span>{(current_time.getHours() % 12 || 12) + " " + (current_time.getHours() >= 12 ? "pm" : "am")}</span>
          </li>
        ));
      }
    }

    return (
      <ul className="heatmap-dates">
        <li className={"day-schedule" + (this.props.selected_day ? " show" : "")}>
          <ul>
            {day_cells}
          </ul>
        </li>
        {cells}
      </ul>
    );
  }
}

export default Scheduler
