import React from 'react'

class TaskEditor extends React.Component {
  state = {
    tasks:[]
  };

  componentDidMount() {
    this.setState({
      tasks: [...this.props.selected_job_data.tasks]
    });
  }

  componentDidUpdate(prevProps) {
    if (this.props.selected_job_data && this.props.selected_job !== prevProps.selected_job) {
      this.setState({
        tasks: [...this.props.selected_job_data.tasks]
      });
    }
  }

  handleTaskClick = (e) => {
    let id=e.currentTarget.dataset.id;
    for (var t=0; t<this.props.selected_job_data.tasks.length; t++) {
      var task = this.props.selected_job_data.tasks[t];
      if (task.id === id) {
        this.props.selected_job_data.tasks[t].completed = !task.completed;
      }
    }
    this.setState({
      tasks: [...this.props.selected_job_data.tasks]
    });
  }


  render() {
    let tasks = [...this.state.tasks];
    let task_nodes = [];

    for (var t=0; t<tasks.length; t++) {
      task_nodes.push((
        <li key={tasks[t].id} className={"task" + (tasks[t].completed ? " completed" : "")} data-id={tasks[t].id} onClick={this.handleTaskClick}>
          <h3>
            <span className="icon-holder"><i className="fas fa-check-circle check"></i><i className="far fa-circle"></i></span>
            <span className="task-name">{tasks[t].name}</span>
          </h3>
        </li>
      ));
    }

    return (
      <div className={"task-editor" + (this.state.loaded?"":" loading")} >
        <div className="menu">
          {(
            <div>
              <button onClick={(e)=>{this.handleNextMonth(e)}}><i className="fas fa-trash"></i><span>Delete Tasks</span></button>
            </div>
          )}
          {(
            <div>
              <button onClick={(e)=>{this.handleNextMonth(e)}}><i className="fas fa-plus"></i><span>Add Task</span></button>
            </div>
          )}
        </div>
        <div className="task-list">
          <ul>{task_nodes}</ul>
        </div>

      </div>
    );
  }
}

export default TaskEditor
