let nextTaskId = 0
export const addTask = ({start, end, title}) => {
  const end_date = new Date(end);
  if (end-start >= 86400000 || end-start === 0) {
    end_date.setDate(end_date.getDate() + 1).toString();
  }
  return ({
    type: 'ADD_TASK',
    id: nextTaskId++,
    task: {
      start:start,
      end:end_date,
      title:title,
    }
  });
}

export const setVisibilityFilter = filter => ({
  type: 'SET_VISIBILITY_FILTER',
  filter
})

export const toggleTodo = id => ({
  type: 'TOGGLE_TODO',
  id
})

export const VisibilityFilters = {
  SHOW_ALL: 'SHOW_ALL',
  SHOW_COMPLETED: 'SHOW_COMPLETED',
  SHOW_ACTIVE: 'SHOW_ACTIVE'
}
