import React from 'react'
import {WEEKEND, DAILY_WORK_HOURS} from '../config/account-settings'
import {DaysInMonth, SameDay} from '../utilities/date'

class Scheduler extends React.Component {
  state = {

  };

  calcDays = () => {
    const days = [];
    const today = new Date();
    const past_number_chart = [3,8,8,0,7,1,7,7,5,6,6,5,5,6,7,8,8,8,8];
    const number_chart = [0,0,0,0,0,1,1,2,2,3,4,5,5,6,7,8,8,8,8];
    const future_number_chart = [0,0,0,0,0,1,1,2,0,3,0,5,0,0,0,0,0,0,8];
    for (var d=0; d<DaysInMonth(this.props.month, this.props.year); d++) {
      if ((d < today.getDate() && ((this.props.month/20)+this.props.year) === ((today.getMonth()/20)+today.getFullYear())) || ((this.props.month/20)+this.props.year) < ((today.getMonth()/20)+today.getFullYear())) {
        days.push(Math.min(past_number_chart[Math.floor(Math.random()*past_number_chart.length)],DAILY_WORK_HOURS));
      }
      else if ((d > today.getDate() + 7 && ((this.props.month/20)+this.props.year) === ((today.getMonth()/20)+today.getFullYear())) || ((this.props.month/20)+this.props.year) > ((today.getMonth()/20)+today.getFullYear())) {
        days.push(Math.min(future_number_chart[Math.floor(Math.random()*future_number_chart.length)],DAILY_WORK_HOURS));
      }
      else {
        days.push(Math.min(number_chart[Math.floor(Math.random()*number_chart.length)],DAILY_WORK_HOURS));
      }
    }
    return days;
  }

  generateTasks = () => {
    let tasks_by_day = [];

    const today = new Date();
    const next_week = new Date(today.getFullYear(), today.getMonth(), today.getDate() + 7);
    const past_number_chart = [3,8,8,0,7,1,7,7,5,6,6,5,5,6,7,8,8,8,8];
    const number_chart = [0,0,0,0,0,1,1,2,2,3,4,5,5,6,7,8,8,8,8];
    const future_number_chart = [0,0,0,0,0,1,1,2,0,3,0,5,0,0,0,0,0,0,8];

    for (var d=0; d<DaysInMonth(this.props.month, this.props.year); d++) {
      let day_tasks = [];
      const current_date = new Date(this.props.year, this.props.month, (d+1));
      if (this.props.task.start_date && SameDay(this.props.task.start_date, current_date) && this.props.task.assigned_user === this.props.user.user_id) {
        day_tasks.push(this.generateTask({...this.props.task}));
      }
      // RANDOMLY ASSIGN A NUMBER OF TASKS BASED ON IF THE CURRENT_DATE IS BEFORE TODAY, WITHIN A WEEK OR BEYOND A WEEK
      const number_of_tasks = (current_date < today) ? Math.min(past_number_chart[Math.floor(Math.random()*past_number_chart.length)],DAILY_WORK_HOURS) :
                              (current_date > next_week) ? Math.min(future_number_chart[Math.floor(Math.random()*future_number_chart.length)],DAILY_WORK_HOURS) :
                              Math.min(number_chart[Math.floor(Math.random()*number_chart.length)],DAILY_WORK_HOURS)
      for (var t=0; t<number_of_tasks; t++) {
        day_tasks.push(0);
      }

      tasks_by_day.push(day_tasks);
    }

    return tasks_by_day;
  }

  componentDidMount() {
    var tasks_by_day = this.generateTasks();
    setTimeout(()=>{
      this.setState({
        days: tasks_by_day
      });
    }, 100);
  }

  componentDidUpdate(prevProps) {
    if (this.props.month !== prevProps.month) {
      this.setState({
        days: this.generateTasks()
      });
    }
  }

  SelectDate = (e) => {
    // let id=parseInt(e.currentTarget.dataset.id,10);
    console.log(e.target.value);
    this.setState({sort: e.target.value});
  }

  render() {
    const cells = [];
    const classes = [
      "tasks-0",
      "tasks-1",
      "tasks-2",
      "tasks-3",
      "tasks-4",
      "tasks-5",
      "tasks-6",
      "tasks-7",
      "tasks-8"
    ];

    if (this.state.days && this.state.days.length === DaysInMonth(this.props.month, this.props.year)) {
      for (var i=0; i<DaysInMonth(this.props.month, this.props.year); i++) {

        const current_date = new Date(this.props.year, this.props.month, (i+1));
        let value =  this.state.days[i].length;
        let weekend = "";

        for (var w=0; w<WEEKEND.length; w++) {
          if (current_date.getDay() === WEEKEND[w]) {
            value = 0;
            weekend = " weekend";
          }
        }
        cells.push((
          <li key={this.props.user.user_id + "_" + i} className={"cell " +
            (classes[Math.min(Math.round(value*8/DAILY_WORK_HOURS),classes.length-1)]) +
            weekend +
            (((current_date.toDateString() === (new Date()).toDateString()) || (((this.props.month/20)+this.props.year) > (((new Date()).getMonth()/20)+(new Date()).getFullYear()) && i===0)) ? " today" : "") +
            (this.props.task.start_date && SameDay(this.props.task.start_date, current_date) && this.props.task.assigned_user === this.props.user.user_id ? " assigned" : "")
          }>{
            value?value+" hrs":""
          }</li>
        ));
      }
    }

    return (
      <ul>
        {cells}
      </ul>
    );
  }
}

export default Scheduler
