import React from 'react'

class TaskEditor extends React.Component {
  state = {
    tasks:[]
  };

  componentDidMount() {
    this.setState({
      tasks: [...this.props.selected_job_data.tasks]
    });
  }

  componentDidUpdate(prevProps) {
    if (this.props.selected_job_data && this.props.selected_job !== prevProps.selected_job) {
      this.setState({
        tasks: [...this.props.selected_job_data.tasks]
      });
    }
  }

  handleTaskClick = (e) => {
    let id=e.currentTarget.dataset.id;
    for (var t=0; t<this.props.selected_job_data.tasks.length; t++) {
      var task = this.props.selected_job_data.tasks[t];
      if (task.id === id) {
        this.props.selected_job_data.tasks[t].completed = !task.completed;
      }
    }
    this.setState({
      tasks: [...this.props.selected_job_data.tasks]
    });
  }


  render() {
    let tasks = [...this.state.tasks];
    let task_nodes = [];

    for (var t=0; t<tasks.length; t++) {
      task_nodes.push((
        <li className={"task" + (tasks[t].completed ? " completed" : "")} data-id={tasks[t].id} onClick={this.handleTaskClick}>
          <h3>
            {tasks[t].completed ? (<i className="fas fa-check-circle"></i>) : (<i className="far fa-circle"></i>)}
            <span>{tasks[t].name}</span>
          </h3>
        </li>
      ));
    }

    return (
      <div className={"task-editor" + (this.state.loaded?"":" loading")} >
        <div className="task-list">
          <ul>{task_nodes}</ul>
        </div>
        <div className="menu">
          <div>
            <i className="fas fa-sort-alpha-down"></i>
            <select onChange={this.selectSort}>
              <option value="trade_name">sort by trade</option>
              <option value="last_name">sort by last name</option>
              <option value="first_name">sort by first name</option>
            </select>

          </div>
          {(
            <div>
              <button onClick={(e)=>{this.handlePrevMonth(e)}}><i className="fas fa-angle-left"></i><span>Previous Month</span></button>
              <button onClick={(e)=>{this.handleGotoToday(e)}}><span>Today</span></button>
              <button onClick={(e)=>{this.handleNextMonth(e)}}><span>Next Month</span><i className="fas fa-angle-right"></i></button>
            </div>
          )}
        </div>
      </div>
    );
  }
}

export default TaskEditor
