import React from 'react'
import ReactDOM from 'react-dom'
import Project from './project'

class Projects extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loaded: false
    };
    this.myRef = React.createRef();
  }
  componentDidMount() {
    setTimeout(()=>{
      this.setState({loaded: true});
    }, 50);
  }
  scrollToDomRef = () => {
    const myDomNode = ReactDOM.findDOMNode(this.myRef.current)
    myDomNode.scrollIntoView()
    // or: window.scrollTo(0, myDomNode.offsetTop)
  }
  render() {
    return (
      <div className={this.state.loaded?"loaded":"unloaded"}>
        <div className="card d1">
          <Project />
        </div>
        <div className="card d2">
          <Project />
        </div>
        <div className="card d3">
          <Project />
        </div>
      </div>
    );
  }
}

export default Projects
