import React from 'react'
import Editor from './editor'
import Scheduler from './scheduler'
import {TasksById} from '../utilities/content'

class TaskDetails extends React.Component {
  state = {
    detail_mode: "schedule"
  }

  changeMode (mode) {
    this.setState({
      detail_mode: mode
    });
  }
  handleTaskClick = (project_id, id) => {
    this.props.handleTaskClick(project_id, id);
  }

  render () {
    return (
      <div className={"task-detail-panel" +
        (this.props.selected_task?"":" open") +
        (" show-content")
      }>
        { this.props.selected_task?(
          <div>
            <div>
              <header>
                <h2>{TasksById[this.props.selected_task].name}</h2>
                <div>
                  <button className={this.state.detail_mode === "schedule"?"selected":""} onClick={()=>{this.changeMode("schedule")}}>schedule task</button>
                  <button className={this.state.detail_mode === "note"?"selected":""} onClick={()=>{this.changeMode("note")}}>take notes</button>
                </div>
              </header>
              <div className="tab-panel">
                <div className={this.state.detail_mode === "note"?"show":""}>
                  {
                    this.state.detail_mode === "note"?
                      (<Editor
                        value="These are some notes!"
                        onChange={(value)=>{console.log(value);}}
                      />)
                    :
                      []
                  }
                </div>
                <div className={this.state.detail_mode === "schedule"?"show":""}>
                  {
                    this.state.detail_mode === "schedule"?
                      (<Scheduler
                        selected_task={this.props.selected_task}
                        default_trade={TasksById[this.props.selected_task].default_trade}
                        handleTaskClick={this.handleTaskClick}
                       />)
                    :
                      []
                  }
                </div>
              </div>
            </div>
          </div>
        ):[]}
      </div>
    );
  }
}

export default TaskDetails
