import React from 'react';
import Project from './project';
import TaskDetails from './task-details';
import {ProjectList} from '../utilities/content'

class Projects extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loaded: false,
      selected_task: null
    };
  }
  componentDidMount() {
    setTimeout(()=>{
      this.setState({loaded: true});
    }, 50);
  }
  selectProject (id, task) {
    this.setState({
      selected_project: id,
      selected_task: task
    });
  }
  render() {
    let projects = [];

    for (let p=0; p<ProjectList.length; p++) {
      projects.push((
        <div key={"project-"+p} className={"card" + (p<5?" d"+(p+1):" d5")}>
          <Project id={p} project_data={ProjectList[p]} onSelectProject={(id, task)=>{
            this.selectProject(id, task);
          }} selected_project={this.state.selected_project===p}/>
        </div>
      ));
    }

    return (
      <div className={this.state.loaded?"loaded":"unloaded"}>
        {this.state.selected_task?
        <div id="project-details" className={"card" + (this.state.selected_task?" show":"")}>
          <Project id={this.state.selected_project} project_data={ProjectList[this.state.selected_project]} onSelectProject={(id, task)=>{
            this.selectProject(id, task);
          }} selected_project={true}/>
          <TaskDetails selected_task={this.state.selected_task} />
        </div>
        : [] }
        <div className={"project-list"+(this.state.selected_task?"":" show")}>
          {projects}
        </div>
      </div>
    );
  }
}

export default Projects
