import React from 'react'
import {WEEKEND, DAILY_WORK_HOURS} from '../config/account-settings'
import {DaysInMonth, SameDay} from '../utilities/date'
import {ProjectList} from '../utilities/content'

class Scheduler extends React.Component {
  state = {
    selected_day:0,
    tasks_by_day:[],
    tasks_by_id:{}
  };


  // generateTasks = () => {
  //   let tasks_by_day = [];
  //   let tasks_by_id = {};
  //
  //   const today = new Date();
  //   const next_week = new Date(today.getFullYear(), today.getMonth(), today.getDate() + 7);
  //   const past_number_chart = [3,8,8,0,7,1,7,7,5,6,6,5,5,6,7,8,8,8,8];
  //   const number_chart = [0,0,0,0,0,1,1,2,2,3,4,5,5,6,7,8,8,8,8];
  //   const future_number_chart = [0,0,0,0,0,1,1,2,0,3,0,5,0,0,0,0,0,0,8];
  //
  //   for (var d=0; d<DaysInMonth(this.props.month, this.props.year); d++) {
  //     let day_tasks = [];
  //     const current_date = new Date(this.props.year, this.props.month, (d+1));
  //     if (this.props.task.start_date && SameDay(this.props.task.start_date, current_date) && this.props.task.assigned_user === this.props.user.user_id) {
  //       // day_tasks.push(this.generateTask({...this.props.task}));
  //     }
  //     // RANDOMLY ASSIGN A NUMBER OF HOURS BASED ON IF THE CURRENT_DATE IS BEFORE TODAY, WITHIN A WEEK OR BEYOND A WEEK
  //     const number_of_hours = (current_date < today) ? Math.min(past_number_chart[Math.floor(Math.random()*past_number_chart.length)],DAILY_WORK_HOURS) :
  //                             (current_date > next_week) ? Math.min(future_number_chart[Math.floor(Math.random()*future_number_chart.length)],DAILY_WORK_HOURS) :
  //                             Math.min(number_chart[Math.floor(Math.random()*number_chart.length)],DAILY_WORK_HOURS);
  //     const number_of_tasks = Math.ceil(Math.random()*number_of_hours)
  //     //
  //     if (number_of_hours>0) {
  //       var last_end_time = 6;
  //       for (var t=0; t<number_of_tasks; t++) {
  //         var start_time = last_end_time+(Math.round(number_of_hours/number_of_tasks)+(Math.random()));
  //         var end_time = start_time+(number_of_hours/number_of_tasks);
  //         var id = "task-"+d+"-"+t;
  //         var task = {
  //           start_date: new Date(this.props.year, this.props.month, (d+1), start_time ),
  //           end_date: new Date(this.props.year, this.props.month, (d+1), end_time, -Math.round(Math.random()*10) ),
  //           assigned_user: this.props.user.user_id,
  //           project_data: ProjectList[Math.floor(Math.random()*ProjectList.length)],
  //           name: "Task",
  //           icon: "fas fa-hammer",
  //           progress: 0,
  //           id: id
  //         };
  //         day_tasks.push(task);
  //         last_end_time = end_time;
  //         tasks_by_id[id] = task;
  //       }
  //     }
  //     tasks_by_day.push(day_tasks);
  //   }
  //   this.setState({
  //     tasks_by_id: tasks_by_id
  //   })
  //   return tasks_by_day;
  // }
  //
  componentDidMount() {
    // var tasks_by_day = this.generateTasks();
    // setTimeout(()=>{
    //   this.setState({
    //     tasks_by_day: tasks_by_day
    //   });
    // }, 100);
  }

  componentDidUpdate(prevProps) {
    if (this.props.month !== prevProps.month) {
      this.setState({
        tasks_by_day: this.generateTasks()
      });
    }
    if (this.props.selected_day && this.props.selected_day !== prevProps.selected_day) {
      this.setState({
        selected_day: this.props.selected_day
      });
    }
  }

  handleDayClick = (e) => {
    const id=parseInt(e.currentTarget.dataset.id,10);
    const index=parseInt(e.currentTarget.dataset.index,10);
    this.props.handleDayClick(id, index);
  }
  handleTaskClick = (e) => {
    const id=e.currentTarget.dataset.id;
    const project_id=e.currentTarget.dataset.project;
    this.props.handleTaskClick(project_id, this.state.tasks_by_id[id]);
  }

  render() {
    const cells = [];
    const day_cells = [];
    const classes = [
      "tasks-0",
      "tasks-1",
      "tasks-2",
      "tasks-3",
      "tasks-4",
      "tasks-5",
      "tasks-6",
      "tasks-7",
      "tasks-8"
    ];

    if (this.state.tasks_by_day && this.state.tasks_by_day.length === DaysInMonth(this.props.month, this.props.year)) {
      for (var i=0; i<DaysInMonth(this.props.month, this.props.year); i++) {

        const current_date = new Date(this.props.year, this.props.month, (i+1));
        let value = 0;
        for (var t=0; t<this.state.tasks_by_day[i].length; t++) {
          const task = this.state.tasks_by_day[i][t];
          const task_duration = (((task.end_date-task.start_date)/1000)/60/60);
          value+=task_duration;
        }
        let weekend = "";

        for (var w=0; w<WEEKEND.length; w++) {
          if (current_date.getDay() === WEEKEND[w]) {
            value = 0;
            weekend = " weekend";
          }
        }
        cells.push((
          <li
            key={this.props.user.user_id + "_" + i}
            data-id={(i+1)}
            data-index={(this.props.index)}
            className={"cell " +
              (classes[Math.min(Math.round(value*8/DAILY_WORK_HOURS),classes.length-1)]) +
              weekend +
              (((current_date.toDateString() === (new Date()).toDateString()) || (((this.props.month/20)+this.props.year) > (((new Date()).getMonth()/20)+(new Date()).getFullYear()) && i===0)) ? " today" : "") +
              (this.props.task.start_date && SameDay(this.props.task.start_date, current_date) && this.props.task.assigned_user === this.props.user.user_id ? " assigned" : "")
          } onClick={this.handleDayClick}
          >{
            value?(Math.round(value*10)/10)+" hrs":""
          }</li>
        ));
      }

      // The Day view
      if (this.state.selected_day && this.state.tasks_by_day[this.state.selected_day-1]) {

        for (var h=0; h<24; h++) {
          // const current_time = new Date(this.props.year, this.props.month, this.state.selected_day, h);
          day_cells.push((
            <li
              key={"hour-"+h}
              className={
                "day-cell"
              }
            >
            </li>
          ));
        }

        //Represent Tasks on Day View
        for (t=0; t<this.state.tasks_by_day[this.state.selected_day-1].length; t++) {
          const task = this.state.tasks_by_day[this.state.selected_day-1][t];
          const task_duration = (((task.end_date-task.start_date)/1000)/60/60/24)*100;
          const task_start = ((task.start_date - new Date(this.props.year, this.props.month, this.state.selected_day))/1000/60/60/24)*100;
          day_cells.push((
            <li
              key={"task-"+t}
              data-id={task.id}
              data-project={task.project_data.id}
              className={
                "task"
              }
              style={{
                left: task_start+"%",
                maxWidth: task_duration+"%",
                backgroundColor: task.project_data.color
              }}
              onClick={
                this.handleTaskClick
              }
            >
              <h4>
                <i className={task.icon}></i>
                <span><strong>{task.name}</strong></span>
              </h4>
              <div>{task.project_data.name}</div>
            </li>
          ));
        }
      }
    }

    return (
      <ul className={(this.props.selected_day ? "show-day" : "")}>
        <li className={"day-schedule"}
          style={{
            // transformOrigin: ((this.state.selected_day/DaysInMonth(this.props.month, this.props.year))*100) + "% 0"
            transition: "opacity .5s ease, transform 0s ease 1s"
          }}
        >
          <ul>
            {day_cells}
          </ul>
        </li>
        {cells}
      </ul>
    );
  }
}

export default Scheduler
