import React from 'react';
import ReactDOM from 'react-dom';
import Project from './project';

class Projects extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loaded: false
    };

    this.projects = [
      React.createRef(),
      React.createRef(),
      React.createRef()
    ];
  }
  componentDidMount() {
    setTimeout(()=>{
      this.setState({loaded: true});
    }, 50);
  }
  scrollToDomRef(id) {
    const myDomNode = ReactDOM.findDOMNode(this.projects[id]);
    myDomNode.scrollIntoView()
    // or: window.scrollTo(0, myDomNode.offsetTop)
  }
  selectProject (id) {
    this.scrollToDomRef(id);
    this.setState({
      selected_project: id
    });
  }
  render() {
    return (
      <div className={this.state.loaded?"loaded":"unloaded"}>
        <div className="card d1" ref={this.projects[0]}>
          <Project id={0} onSelectProject={(id)=>{
            this.selectProject(id);
          }}
          selected_project={
            this.state.selected_project === 0
          } />
        </div>
        <div className="card d2" ref={this.projects[1]}>
          <Project id={1} onSelectProject={(id)=>{
            this.selectProject(id);
          }}
          selected_project={
            this.state.selected_project === 1
          } />
        </div>
        <div className="card d3" ref={this.projects[2]}>
          <Project id={2} onSelectProject={(id)=>{
            this.selectProject(id);
          }}
          selected_project={
            this.state.selected_project === 2
          } />
        </div>
      </div>
    );
  }
}

export default Projects
